parameter_types! {
	pub ERC20SlotZero: H160 = H160::from_str("0x22D598E0a9a1b474CdC7c6fBeA0B4F83E12046a9").unwrap();
	pub ZeroSlot : H256 = H256::from_low_u64_be(0);
	pub ChainId: u64 = 20180427;
}

pub fn new_test_ext() -> sp_io::TestExternalities {
	let mut t = frame_system::GenesisConfig::<Test>::default()
		.build_storage()
		.unwrap();

	pallet_evm::GenesisConfig::<Test> {
		_marker: Default::default(),
		accounts: {
			let mut map = BTreeMap::new();
			map.insert(
				ERC20SlotZero::get(),
				fp_evm::GenesisAccount {
					balance: Default::default(),
					code: Vec::<u8>::from_hex("").unwrap(),
					nonce: Default::default(),
					storage: Default::default(),
				},
			);
			map
		},
	}.assimilate_storage(&mut t)
	.expect("Failed to build GenesisConfig for Evm pallet.");

	pallet_evm_chain_id::GenesisConfig::<Test> {
		_marker: Default::default(),
		chain_id: ChainId::get(),
	}
	.assimilate_storage(&mut t)
	.expect("Failed to build GenesisConfig for EVMChainId pallet.");

	t.into()
}
